import { FC, useState } from 'react';
import '../styles/TaskBoard.css';
import { Ticket, TicketStatus } from '../types/system';

const TaskBoard: FC = () => {
  const [tasks, setTasks] = useState<Ticket[]>([]);

  const columns: TicketStatus[] = ['DRAFT', 'PENDING', 'APPROVED', 'REJECTED'];

  return (
    <div className="task-board">
      {columns.map((status) => (
        <div key={status} className="task-column">
          <h3 className="column-title">{status}</h3>
          <div className="task-list">
            {tasks
              .filter((task) => task.status === status)
              .map((task) => (
                <div key={task.id} className="task-card">
                  <div className="task-priority" data-priority={task.department}></div>
                  <h4>{task.title}</h4>
                  <p>{task.description}</p>
                  <div className="task-meta">
                    <span className="assignee">{task.requester.fullName}</span>
                    <span className="due-date">{task.requestDate}</span>
                  </div>
                </div>
              ))}
          </div>
          <button className="add-task-btn">+ Add Ticket</button>
        </div>
      ))}
    </div>
  );
};

export default TaskBoard;
