import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Ticket, User } from '../../types/system';
import '../../styles/tickets/MyTickets.css';

interface MyTicketsProps {
  tickets: Ticket[];
  currentUser: User;
}

const MyTickets: React.FC<MyTicketsProps> = ({ tickets, currentUser }) => {
  const navigate = useNavigate();

  // Filter tickets based on user role
  const filteredTickets = tickets.filter(ticket => {
    if (!ticket) return false;
    if (currentUser.role === 'ADMIN') return true;
    if (currentUser.role === 'APPROVER') {
      return ticket.status === 'PENDING' || ticket.requesterId === currentUser.id;
    }
    return ticket.requesterId === currentUser._id;
  });

  const formatDate = (dateString: string | undefined) => {
    if (!dateString) return 'N/A';
    try {
      return new Date(dateString).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return 'Invalid Date';
    }
  };

  const getStatusClass = (status: string | undefined) => {
    switch (status) {
      case 'APPROVED': return 'status-approved';
      case 'REJECTED': return 'status-rejected';
      case 'PENDING': return 'status-pending';
      default: return 'status-draft';
    }
  };

  const formatId = (id: string | undefined) => {
    if (!id) return 'N/A';
    return id;
  };

  const handleRowClick = (ticketId: string) => {
    if (ticketId) {
      navigate(`/tickets/${ticketId}`);
    }
  };

  return (
    <div className="my-tickets">
      <h2>My Tickets</h2>
      <div className="tickets-table-container">
        <table className="tickets-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Department</th>
              <th>Status</th>
              <th>Request Date</th>
              <th>Machine</th>
            </tr>
          </thead>
          <tbody>
            {filteredTickets.map(ticket => (
              <tr 
                key={ticket._id || Math.random().toString()}
                onClick={() => ticket._id && handleRowClick(ticket._id)}
                className="ticket-row"
              >
                <td>{formatId(ticket._id)}</td>
                <td>{ticket.title || 'N/A'}</td>
                <td>{ticket.department || 'N/A'}</td>
                <td>
                  <span className={`status-badge ${getStatusClass(ticket.status)}`}>
                    {ticket.status || 'DRAFT'}
                  </span>
                </td>
                <td>{formatDate(ticket.requestDate)}</td>
                <td>{ticket.chemicalConfig?.machineName || 'N/A'}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {filteredTickets.length === 0 && (
          <div className="no-tickets">
            <p>No tickets found</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default MyTickets;
