import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Header.css';
import { User } from '../types/system';

interface HeaderProps {
  onCreateRequest: () => void;
  currentUser: User;
  onLogout: () => void;
}

const Header: React.FC<HeaderProps> = ({ onCreateRequest, currentUser, onLogout }) => {
  const navigate = useNavigate();

  const handleCreateRequest = () => {
    navigate('/tickets/create');
  };

  return (
    <>
      <div className="top-nav">
        <div className="top-nav-items">
          <div className="top-nav-left">
            <span onClick={() => navigate('/')}>Dashboard</span>
            <span onClick={() => navigate('/tickets')}>My Requests</span>
            {currentUser.role === 'APPROVER' && <span onClick={() => navigate('/approvals')}>Approvals</span>}
            {currentUser.role === 'ADMIN' && <span onClick={() => navigate('/reports')}>Reports</span>}
          </div>
          <div className="top-nav-right">
            <button className="nav-button" onClick={handleCreateRequest}>Create Request</button>
            <button className="nav-button">View Status</button>
            <span className="user-info">
              {currentUser.fullName} ({currentUser.role})
            </span>
            <button className="nav-button logout-button" onClick={onLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>
      <header className="header">
        <div className="logo" onClick={() => navigate('/')}>
          <span>ChemReq</span>
        </div>
        <nav className="nav-menu">
          <ul>
            <li onClick={handleCreateRequest}>New Request</li>
            <li onClick={() => navigate('/tickets')}>My Tickets</li>
            {currentUser.role === 'APPROVER' && <li onClick={() => navigate('/approvals')}>Pending Approval</li>}
            <li onClick={() => navigate('/history')}>History</li>
            <li onClick={() => navigate('/settings')}>Settings</li>
            <li onClick={() => navigate('/help')}>Help</li>
          </ul>
        </nav>
        <div className="language-selector">
          <span>EN</span>
        </div>
      </header>
    </>
  );
};

export default Header;
