import React from 'react';
import '../styles/Header.css';
import { User } from '../types/system';

interface HeaderProps {
  onCreateRequest: () => void;
  currentUser: User;
  onLogout: () => void;
}

const Header: React.FC<HeaderProps> = ({ onCreateRequest, currentUser, onLogout }) => {
  return (
    <>
      <div className="top-nav">
        <div className="top-nav-items">
          <div className="top-nav-left">
            <span>Dashboard</span>
            <span>My Requests</span>
            {currentUser.role === 'APPROVER' && <span>Approvals</span>}
            {currentUser.role === 'ADMIN' && <span>Reports</span>}
          </div>
          <div className="top-nav-right">
            <button className="nav-button" onClick={onCreateRequest}>Create Request</button>
            <button className="nav-button">View Status</button>
            <span className="user-info">
              {currentUser.fullName} ({currentUser.role})
            </span>
            <button className="nav-button logout-button" onClick={onLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>
      <header className="header">
        <div className="logo">
          <span>ChemReq</span>
        </div>
        <nav className="nav-menu">
          <ul>
            <li onClick={onCreateRequest}>New Request</li>
            <li>My Tickets</li>
            {currentUser.role === 'APPROVER' && <li>Pending Approval</li>}
            <li>History</li>
            <li>Settings</li>
            <li>Help</li>
          </ul>
        </nav>
        <div className="language-selector">
          <span>EN</span>
        </div>
      </header>
    </>
  );
};

export default Header;
