import React from 'react';
import '../styles/Products.css';

interface ProductsProps {
  onCreateRequest: () => void;
}

const Products: React.FC<ProductsProps> = ({ onCreateRequest }) => {
  const features = [
    { title: 'Request Management', icon: 'üìù' },
    { title: 'Approval Workflow', icon: '‚úÖ' },
    { title: 'Chemical Config', icon: '‚öóÔ∏è' },
    { title: 'File Attachments', icon: 'üìé' },
    { title: 'Status Tracking', icon: 'üìä' },
    { title: 'Create New Request', icon: '‚ûï', onClick: onCreateRequest }
  ];

  return (
    <section className="products">
      <h2>System Features</h2>
      <div className="products-grid">
        {features.map((feature, index) => (
          <div 
            key={index} 
            className={`product-card ${feature.onClick ? 'clickable' : ''}`}
            onClick={feature.onClick}
          >
            <span className="product-icon">{feature.icon}</span>
            <h3>{feature.title}</h3>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Products;
