import { FC, useState } from 'react';
import './App.css';
import Header from './components/Header';
import Hero from './components/Hero';
import Products from './components/Products';
import TicketCreation from './components/tickets/TicketCreation';
import TaskBoard from './components/TaskBoard';
import { User, Department, UserRole, Ticket, TicketStatus } from './types/system';

const App: FC = () => {
  const [showTicketCreation, setShowTicketCreation] = useState(false);
  const [tickets, setTickets] = useState<Ticket[]>([]);

  // Mock current user - in real app this would come from authentication
  const currentUser: User = {
    id: '1',
    username: 'john.doe',
    email: 'john.doe@example.com',
    fullName: 'John Doe',
    role: 'REQUESTER' as UserRole,
    department: 'PRODUCTION' as Department,
    isActive: true,
    createdAt: new Date().toISOString()
  };

  const handleCreateRequest = () => {
    setShowTicketCreation(true);
  };

  const handleTicketSubmit = async (ticketData: Omit<Ticket, 'id' | 'status' | 'createdAt' | 'updatedAt'>) => {
    try {
      // Create a new ticket with generated ID and initial status
      const newTicket: Ticket = {
        ...ticketData,
        id: Date.now().toString(), // In real app, this would come from backend
        status: 'DRAFT' as TicketStatus,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };

      // Add the new ticket to our state
      setTickets(prevTickets => [...prevTickets, newTicket]);
      setShowTicketCreation(false);

      // Show success message
      alert('Ticket created successfully!');
    } catch (error) {
      console.error('Error creating ticket:', error);
      alert('Failed to create ticket. Please try again.');
    }
  };

  const handleTicketStatusChange = (ticketId: string, newStatus: TicketStatus) => {
    setTickets(prevTickets =>
      prevTickets.map(ticket =>
        ticket.id === ticketId
          ? { ...ticket, status: newStatus, updatedAt: new Date().toISOString() }
          : ticket
      )
    );
  };

  return (
    <div className="app">
      <Header 
        onCreateRequest={handleCreateRequest}
        currentUser={currentUser}
      />
      <main>
        {showTicketCreation ? (
          <TicketCreation 
            currentUser={currentUser}
            onSubmit={handleTicketSubmit}
          />
        ) : (
          <>
            <Hero onCreateRequest={handleCreateRequest} />
            <Products />
            <TaskBoard 
              tickets={tickets}
              onStatusChange={handleTicketStatusChange}
              currentUser={currentUser}
            />
          </>
        )}
      </main>
      <button className="accessibility-btn" aria-label="Accessibility Options">â™¿</button>
    </div>
  );
};

export default App;
