import { FC } from 'react';
import '../styles/TaskBoard.css';
import { Ticket, TicketStatus, User } from '../types/system';

interface TaskBoardProps {
  tickets: Ticket[];
  onStatusChange: (ticketId: string, newStatus: TicketStatus) => void;
  currentUser: User;
}

const TaskBoard: FC<TaskBoardProps> = ({ tickets, onStatusChange, currentUser }) => {
  const columns: TicketStatus[] = ['DRAFT', 'PENDING', 'APPROVED', 'REJECTED'];

  const canChangeStatus = (ticket: Ticket): boolean => {
    if (currentUser.role === 'ADMIN') return true;
    if (currentUser.role === 'APPROVER' && ticket.status === 'PENDING') return true;
    if (currentUser.role === 'REQUESTER' && ticket.requesterId === currentUser.id && ticket.status === 'DRAFT') return true;
    return false;
  };

  const getNextStatus = (currentStatus: TicketStatus): TicketStatus | null => {
    switch (currentStatus) {
      case 'DRAFT': return 'PENDING';
      case 'PENDING': return currentUser.role === 'APPROVER' ? 'APPROVED' : null;
      default: return null;
    }
  };

  return (
    <div className="task-board">
      {columns.map((status) => (
        <div key={status} className="task-column">
          <h3 className="column-title">{status}</h3>
          <div className="task-list">
            {tickets
              .filter((ticket) => ticket.status === status)
              .map((ticket) => (
                <div key={ticket.id} className="task-card">
                  <div className="task-priority" data-priority={ticket.department}></div>
                  <h4>{ticket.title}</h4>
                  <p>{ticket.description}</p>
                  <div className="task-meta">
                    <span className="assignee">{ticket.requester.fullName}</span>
                    <span className="due-date">{new Date(ticket.requestDate).toLocaleDateString()}</span>
                  </div>
                  {canChangeStatus(ticket) && getNextStatus(ticket.status) && (
                    <button 
                      className="status-change-btn"
                      onClick={() => {
                        const nextStatus = getNextStatus(ticket.status);
                        if (nextStatus) {
                          onStatusChange(ticket.id, nextStatus);
                        }
                      }}
                    >
                      Move to {getNextStatus(ticket.status)}
                    </button>
                  )}
                </div>
              ))}
          </div>
          <button className="add-task-btn">+ Add Ticket</button>
        </div>
      ))}
    </div>
  );
};

export default TaskBoard;
