import { FC, useState, useEffect } from 'react';
import '../styles/TaskBoard.css';
import { Ticket, TicketStatus, User } from '../types/system';
import { DragDropContext, Droppable, Draggable, DropResult } from 'react-beautiful-dnd';
import Toast from './Toast';

interface TaskBoardProps {
  tickets: Ticket[];
  onStatusChange: (ticketId: string, newStatus: TicketStatus) => void;
  currentUser: User;
}

// StrictMode compatibility workaround for react-beautiful-dnd
const useDndStrictModeWorkaround = () => {
  const [enabled, setEnabled] = useState(false);

  useEffect(() => {
    // We need to delay enabling the drag-n-drop until after the initial render
    // to work around an issue with StrictMode in React 18
    const timeout = setTimeout(() => {
      setEnabled(true);
    }, 0);

    return () => clearTimeout(timeout);
  }, []);

  return enabled;
};

const TaskBoard: FC<TaskBoardProps> = ({ tickets, onStatusChange, currentUser }) => {
  const columns: TicketStatus[] = ['DRAFT', 'PENDING', 'APPROVED', 'REJECTED'];
  const [isDragging, setIsDragging] = useState(false);
  const [toast, setToast] = useState<{ message: string; visible: boolean }>({
    message: '',
    visible: false
  });

  // Use the StrictMode workaround
  const isDndEnabled = useDndStrictModeWorkaround();

  // Clean up drag state on unmount
  useEffect(() => {
    return () => {
      setIsDragging(false);
    };
  }, []);

  // Check if the user can move the ticket from source to destination status
  const canMoveTicket = (
    ticket: Ticket,
    sourceStatus: TicketStatus,
    destinationStatus: TicketStatus
  ): boolean => {
    const role = currentUser.role;

    // ADMIN can move tickets freely between any status
    if (role === 'ADMIN') return true;

    // APPROVER can only approve or reject tickets from PENDING status
    if (role === 'APPROVER') {
      if (sourceStatus === 'PENDING' && (destinationStatus === 'APPROVED' || destinationStatus === 'REJECTED')) {
        return true;
      }
      return false;
    }

    // REQUESTER can only move their own tickets from DRAFT to PENDING
    if (role === 'REQUESTER') {
      if (ticket.requesterId === currentUser._id && sourceStatus === 'DRAFT' && destinationStatus === 'PENDING') {
        return true;
      }
      return false;
    }

    return false;
  };

  const getRequesterName = (ticket: Ticket): string => {
    return ticket.requester?.fullName || 'Unknown User';
  };

  const handleDragEnd = (result: DropResult) => {
    setIsDragging(false);

    const { draggableId, source, destination } = result;

    // Drop outside any droppable area or same position
    if (!destination || 
        (source.droppableId === destination.droppableId && 
         source.index === destination.index)) {
      return;
    }

    // Extract the ticket ID from the draggableId (remove 'ticket-' prefix)
    const ticketId = draggableId.replace('ticket-', '');

    // Extract the status from the droppableId (remove 'column-' prefix)
    const sourceStatus = source.droppableId.replace('column-', '') as TicketStatus;
    const destinationStatus = destination.droppableId.replace('column-', '') as TicketStatus;

    const ticket = tickets.find(t => t._id === ticketId);

    if (!ticket) return;

    // Check if the user has permission to move the ticket
    if (canMoveTicket(ticket, sourceStatus, destinationStatus)) {
      onStatusChange(ticketId, destinationStatus);
    } else {
      // Show toast notification for permission error
      setToast({
        message: `You don't have permission to move this ticket to ${destinationStatus} status`,
        visible: true
      });
    }
  };

  const handleDragStart = () => {
    setIsDragging(true);
  };

  // Handle closing the toast
  const handleCloseToast = () => {
    setToast({ ...toast, visible: false });
  };

  // Create a simplified board without drag-and-drop
  const renderSimpleBoard = () => {
    return (
      <div className="task-board">
        {columns.map((status) => (
          <div key={status} className="task-column">
            <h3 className="column-title">{status}</h3>
            <div className="task-list">
              {tickets
                .filter((ticket) => ticket.status === status)
                .map((ticket) => (
                  <div key={ticket._id} className="task-card">
                    <div className="task-priority" data-priority={ticket.department}></div>
                    <h4>{ticket.title}</h4>
                    <p>{ticket.description}</p>
                    <div className="task-meta">
                      <span className="assignee">{getRequesterName(ticket)}</span>
                      <span className="due-date">
                        {ticket.requestDate ? new Date(ticket.requestDate).toLocaleDateString() : 'No date'}
                      </span>
                    </div>
                  </div>
                ))}
            </div>
            <button className="add-task-btn">+ Add Ticket</button>
          </div>
        ))}
      </div>
    );
  };

  // If DnD is not enabled yet, render a simplified board
  if (!isDndEnabled) {
    return (
      <>
        {toast.visible && (
          <Toast 
            message={toast.message} 
            type="error" 
            onClose={handleCloseToast} 
          />
        )}
        {renderSimpleBoard()}
      </>
    );
  }

  return (
    <>
      {toast.visible && (
        <Toast 
          message={toast.message} 
          type="error" 
          onClose={handleCloseToast} 
        />
      )}

      <DragDropContext onDragEnd={handleDragEnd} onDragStart={handleDragStart}>
        <div className="task-board">
          {columns.map((columnStatus) => {
            // Create a consistent droppable ID
            const droppableId = `column-${columnStatus}`;

            return (
              <div key={columnStatus} className="task-column">
                <h3 className="column-title">{columnStatus}</h3>
                <Droppable droppableId={droppableId} key={droppableId}>
                  {(provided, snapshot) => {
                    // Filter tickets for this column
                    const columnTickets = tickets.filter(ticket => ticket.status === columnStatus);

                    return (
                      <div
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                        className={`task-list ${snapshot.isDraggingOver ? 'drop-allowed' : ''}`}
                      >
                        {columnTickets.map((ticket, index) => {
                          // Determine if this ticket can be dragged
                          const isDragDisabled = 
                            (currentUser.role === 'REQUESTER' && 
                            (ticket.requesterId !== currentUser._id || ticket.status !== 'DRAFT')) ||
                            (currentUser.role === 'APPROVER' && ticket.status !== 'PENDING');

                          // Create a consistent draggable ID
                          const draggableId = `ticket-${ticket._id}`;

                          return (
                            <Draggable 
                              key={draggableId} 
                              draggableId={draggableId}
                              index={index}
                              isDragDisabled={isDragDisabled}
                            >
                              {(provided, snapshot) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                  className={`task-card ${snapshot.isDragging ? 'is-dragging' : ''} ${isDragDisabled ? 'not-draggable' : ''}`}
                                >
                                  <div className="task-priority" data-priority={ticket.department}></div>
                                  <h4>{ticket.title}</h4>
                                  <p>{ticket.description}</p>
                                  <div className="task-meta">
                                    <span className="assignee">{getRequesterName(ticket)}</span>
                                    <span className="due-date">
                                      {ticket.requestDate ? new Date(ticket.requestDate).toLocaleDateString() : 'No date'}
                                    </span>
                                  </div>
                                </div>
                              )}
                            </Draggable>
                          );
                        })}
                        {provided.placeholder}
                      </div>
                    );
                  }}
                </Droppable>
                <button className="add-task-btn">+ Add Ticket</button>
              </div>
            );
          })}
        </div>
      </DragDropContext>
    </>
  );
};

export default TaskBoard;
