import React from 'react';
import { Outlet } from 'react-router-dom';
import Header from './Header';
import { User } from '../types/system';

interface LayoutProps {
  currentUser: User;
  onLogout: () => void;
  onCreateRequest: () => void;
}

const Layout: React.FC<LayoutProps> = ({ currentUser, onLogout, onCreateRequest }) => {
  return (
    <div className="app">
      <Header 
        onCreateRequest={onCreateRequest}
        currentUser={currentUser}
        onLogout={onLogout}
      />
      <main>
        <Outlet />
      </main>
      <button className="accessibility-btn" aria-label="Accessibility Options">â™¿</button>
    </div>
  );
};

export default Layout;
